var objCodes = require("./codes");



/**
 * Returns the name of the Country based on its 
 * Country code
 * @param strCode code of the country
 * @return {String} name of the Country
 *
 **/
exports.getNameByCode = function(strCode){

  for(var i =0; i<objCodes.length;i++){
      if(objCodes[i].code==strCode){
       return objCodes[i].name;
      }
  }
}


